const root=document.getElementById("root");function getElem(e){return document.getElementById(e).value}function checkStr(e){return-1===e.indexOf("e")&&-1===e.indexOf("<")&&e.indexOf("√")&&-1===e.indexOf("^")?e:(newStr=e.replace("e","Math.exp").replace("<","").replace("√","Math.sqrt").replace("^","**"),newStr)}function add(e){let t=getElem("result");t+=e,document.getElementById("result").value=t}function clean(){document.getElementById("result").value=""}function back(){let e=getElem("result");e=e.slice(0,e.length-1),document.getElementById("result").value=e}function calculate(){let result=getElem("result");document.getElementById("result").value=eval(checkStr(result))}root.addEventListener("click",({target:e})=>{if("BUTTON"!==e.tagName)return;const{action:t}=e.dataset;if(t)switch(t){case"calculate":calculate();break;case"clean":clean();break;case"back":back()}else checkStr(e.innerHTML),add(e.innerHTML)});