function getElem(e){return document.getElementById(e).value}function checkStr(e){return-1===e.indexOf("e")?e:(newStr=e.replace("e","Math.exp"),newStr)}function add(e){let t=getElem("result");t+=e,document.getElementById("result").value=t}function clean(){document.getElementById("result").value=""}function back(){let e=getElem("result");e=e.slice(0,e.length-1),document.getElementById("result").value=e}function calculate(){let result=getElem("result");document.getElementById("result").value=eval(checkStr(result))}