const taskList=document.getElementById("data-list"),app=document.getElementById("main");function getValue(t){return task=document.getElementById(t).value}function addTask(t){const e=document.createElement("li");e.className="main__data-item",e.innerHTML=`<button class="btn btn-checkbox" data-action="check">&#10004</button>\n                      <p class="main__todo-task">${t}</p>\n                      <div class="main__btn-wrapper">\n                          <button class="btn btn-important" data-action="highlight">&#9733</button>\n                          <button class="btn btn-delete" data-action="delete">&times</button>\n                      </div>`,taskList.prepend(e),saveTasks(),document.getElementById("main__input").value=""}function deleteTask(t){t.remove(),saveTasks()}function completeTask(t){toggleClass(t,"btn-checkbox_active"),toggleClass(t.parentNode,"completed")}function toggleClass(t,e){t.classList.toggle(e),saveTasks()}function saveTasks(){const t=document.querySelector("ul");localStorage.setItem("list",t.innerHTML)}function loadTasks(){taskList.innerHTML=localStorage.getItem("list")}app.addEventListener("click",({target:t})=>{if("BUTTON"!==t.tagName)return;const{action:e}=t.dataset;if(console.log(t.parentNode),e)switch(e){case"add":getValue("main__input"),addTask(task);break;case"check":completeTask(t);break;case"delete":deleteTask(t.parentNode.parentNode);break;case"highlight":toggleClass(t.parentNode.parentNode,"bold")}}),document.addEventListener("DOMContentLoaded",()=>{loadTasks()});