const taskList=document.getElementById("data-list"),app=document.getElementById("main");function getValue(t){return task=document.getElementById(t).value}function addTask(t){const e=document.createElement("li");e.className="main__data-item",e.innerHTML=`<button class="btn btn-checkbox" data-action="check">&#10004</button>\n                      <p class="main__todo-task">${t}</p>\n                      <button class="btn btn-important" data-action="highlight">&#9733</button>\n                      <button class="btn btn-delete" data-action="delete">&times</button>`,taskList.prepend(e),saveTasks(),document.getElementById("main__input").value=""}function deleteTask(t){t.remove(),saveTasks()}function completeTask(t){toggleClass(t,"btn-checkbox_active"),toggleClass(t.parentNode,"completed")}function highlightTask(t){toggleClass(t,"highlighted"),toggleClass(t.previousElementSibling,"bold")}function toggleClass(t,e){t.classList.toggle(e),saveTasks()}function saveTasks(){localStorage.setItem("list",taskList.innerHTML)}function loadTasks(){taskList.innerHTML=localStorage.getItem("list")}app.addEventListener("click",({target:t})=>{if("BUTTON"!==t.tagName)return;const{action:e}=t.dataset;if(e)switch(e){case"add":getValue("main__input")&&addTask(task);break;case"check":completeTask(t);break;case"delete":deleteTask(t.parentNode);break;case"highlight":highlightTask(t)}}),document.addEventListener("DOMContentLoaded",()=>{loadTasks()});